from PyQt5 import QtWidgets, QtCorefrom PyQt5.QtWidgets import QStylefrom PyQt5.QtCore import Qtclass UiWindow(QtWidgets.QWidget):    def setupUi(self, MainWindow):        self.setWindowTitle("oni-плеер")        self.setGeometry(350, 100, 700, 500)        self.video_widget = QtWidgets.QLabel()        self.video_widget.setScaledContents(True)        self.video_widget.setAlignment(QtCore.Qt.AlignCenter)        self.open_btn = QtWidgets.QPushButton('Открыть')        self.play_btn = QtWidgets.QPushButton()        self.play_btn.setEnabled(False)        self.play_btn.setIcon(self.style().standardIcon(QStyle.SP_MediaPlay))        self.forward_frame = QtWidgets.QPushButton()        self.forward_frame.setEnabled(False)        self.forward_frame.setIcon(self.style().standardIcon(QStyle.SP_MediaSkipForward))        self.back_frame = QtWidgets.QPushButton()        self.back_frame.setEnabled(False)        self.back_frame.setIcon(self.style().standardIcon(QStyle.SP_MediaSkipBackward))        self.slider = QtWidgets.QSlider(Qt.Horizontal)        self.slider.setRange(0, 0)        self.check_box = QtWidgets.QCheckBox()        self.check_box.setEnabled(False)        self.check_box.setText('Цвет')        self.label = QtWidgets.QLabel()        self.label.setSizePolicy(QtWidgets.QSizePolicy.Preferred, QtWidgets.QSizePolicy.Maximum)        hbox_layout = QtWidgets.QHBoxLayout()        hbox_layout.setContentsMargins(0, 0, 0, 0)        hbox_layout.addWidget(self.open_btn)        hbox_layout.addWidget(self.check_box)        hbox_layout.addWidget(self.back_frame)        hbox_layout.addWidget(self.play_btn)        hbox_layout.addWidget(self.forward_frame)        hbox_layout.addWidget(self.slider)        vbox_layout = QtWidgets.QVBoxLayout()        vbox_layout.addWidget(self.video_widget)        vbox_layout.addLayout(hbox_layout)        vbox_layout.addWidget(self.label)        self.setLayout(vbox_layout)